import React from "react";
import "./movieDetails.css";

export default ({ movie }) => {
  if(movie.budget !== 0)
    return (
      <>
        <h4>Overview</h4>
        <p>{movie.overview}</p>
        <ul className="list-group list-group-horizontal">
          <li key="ruh" className="list-group-item list-group-item-dark">
            Runtime (min.)
          </li>
          <li key="rut" className="list-group-item ">
            {movie.runtime}
          </li>
          <li key="rdh" className="list-group-item list-group-item-dark">
            Release Date
          </li>
          <li key="rdv" className="list-group-item ">
            {movie.release_date}
          </li>
        </ul>

        <ul className="list-group list-group-horizontal">
          <li key="gh" className="list-group-item list-group-item-dark">
            Genres
          </li>
          {movie.genres.map(g => (
            <li key={g.name} className="list-group-item">
              {g.name}
            </li>
          ))}
        </ul>
        <ul className="list-group list-group-horizontal">
          <li key="slh" className="list-group-item list-group-item-dark">
            Spoken Languages
          </li>
          {movie.spoken_languages.map(lang => (
            <li key={lang.name} className="list-group-item">
              {lang.name}
            </li>
          ))}
        </ul>
        <ul className="list-group list-group-horizontal">
          <li key="pch" className="list-group-item list-group-item-dark">
            Production Companies
          </li>
          {movie.production_companies.map(pc => (
            <li key={pc.name} className="list-group-item">
              {pc.name}
            </li>
          ))}
        </ul>
        <ul className="list-group list-group-horizontal">
          <li key="pcoh" className="list-group-item list-group-item-dark">
            Production Countries
          </li>
          {movie.production_countries.map(pc => (
            <li key={pc.name} className="list-group-item">
              {pc.name}
            </li>
          ))}
        </ul>
        <ul className="list-group list-group-horizontal">
          <li key="mbh" className="list-group-item list-group-item-dark">
            Movie Budget
          </li>
          <li key="mbhh" className="list-group-item ">
            {movie.budget}
          </li>
          <li key="msh" className="list-group-item list-group-item-dark">
            Movie Status
          </li>
          <li key="msv" className="list-group-item ">
            {movie.status}
          </li>
        </ul>
      </>
    );
};
