import React, { useEffect, useState } from "react";
import { getMovieCredits } from "../../api/tmdb-api";

export default ({ movie }) => {
    const [credits, setCredits] = useState([]);

    useEffect(() => {
        getMovieCredits(movie.id).then(credits => {
            setCredits(credits);
        });
    }, []);
    return (
        <>
            <h4>Casts</h4>
        </>
    );

};
