import React, { useEffect, useState } from "react";
import { getMovieProviders } from "../../api/tmdb-api";

export default ({ movie }) => {
    const [prov, setProviders] = useState([]);

    useEffect(() => {
        getMovieProviders(movie.id).then(prov => {
            setProviders(prov.buy);
        });
    }, []);

    return ( 
        <div>
            <h4>Where to buy in Ireland</h4>
            {prov.map(p => {
                return (
                    <div>
                        {p.name}
                    </div>
                );
            })}
        </div>
    );
};
