import React, { useEffect, useState } from "react";
import { getMovieCredits } from "../../api/tmdb-api";

export default ({ movie }) => {
    const [casts, setCasts] = useState([]);

    useEffect(() => {
        getMovieCredits(movie.id).then(casts => {
            setCasts(casts);
        });
    }, []);
    return (
        <h4>Cast</h4>
        
    );
};
